  align-items: center;
  flex-direction: column;
}

.header {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  .header-icon {
    display: flex;
    align-items: center;
    margin-right: 8px;
    margin-top: -13px;
    color: var(--mat-sys-on-surface);
  }
  .header-title {
    font-family: Inter;
    font-weight: 500;
    font-size: 16px;
    line-height: 180%;
    letter-spacing: 0%;
    vertical-align: middle;
    margin-bottom: 10px;

    font: var(--mat-sys-title-medium);
    color: var(--mat-sys-on-surface);
    .header-subtitle {
      font-family: Inter;
      font-weight: 300;
      font-size: 14px;
      line-height: 24px;
      letter-spacing: 0px;
      vertical-align: middle;

      font: var(--mat-sys-title-small);
      color: var(--mat-sys-on-surface-variant);
    }
  }
}

.status {
  display: flex;
  align-items: center;
  width: 185;
  height: 40;
  padding-right: 24px;
  padding-left: 24px;
  gap: 8px;
  color: #ffffff;
  text-align: center;
  .status-power {
    width: 40;
    height: 40;
    border-radius: 8px;
    padding-right: 8px;
    padding-left: 8px;
    background: var(--Base-colors-Gray-400, #a7a7a7);
  }
  .status-max-load {
    width: 40;
    height: 40;
    border-radius: 8px;
    padding-right: 8px;
    padding-left: 8px;
    background: var(--Base-colors-Gray-400, #a7a7a7);
  }
  .status-row {
    display: inline-flex;
  }
}

.alerts {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 70%;
  height: 40;
  border-radius: 8px;
  padding: 8px;
  margin-top: 8px;
  color: #ffffff;
  cursor: pointer;
  background: var(--Base-colors-Main-orange, #ef942f);
  mat-icon {
    font-size: 12px;
    margin-right: -12px;
    margin-bottom: -12px;
    margin-left: 2px;
  }
}

mat-divider {
  width: 100%;
  margin: 6px;
  border-color: var(--mat-sys-tertiary-fixed-dim);
}

.content {
  flex: 1;
  width: 100%;
  display: grid;
  .content-item {
    flex: 1;
    display: inline-flex;
    align-items: center;
    justify-content: space-between;
    padding: 4px 0;
    width: 197;
    height: 20;
    gap: 6px;
    color: var(--mat-sys-on-surface);
    .content-item-label {
      font: var(--mat-sys-label-large);
      font-weight: 700;
      mat-icon {
        font-size: 20px;
        width: 20px;
        height: 20px;
        margin-right: 6px;
        margin-bottom: -6px;
      }
    }
    .content-item-value {
      font: var(--mat-sys-label-large);
      display: flex;
      align-items: center;
    }
  }
}

.footer {
  cursor: pointer;
