﻿using BJB.TargetSettingTool.Domain.InvestmentCommitteeDecision.ValueObjects;
using BJB.TargetSettingTool.Domain.InvestmentCommitteeDecision.Enums;
using System.ComponentModel;
using System.Runtime.CompilerServices;

[assembly: InternalsVisibleTo("BJB.TargetSettingTool.Domain.Tests")]

namespace BJB.TargetSettingTool.Domain.InvestmentCommitteeDecision.Entities;

public class Trade
{
    public Guid Id { get; }
    public InstrumentId InstrumentId { get; }
    public TradeWeight RedditoDifensivo { get; }
    public TradeWeight Income { get; }
    public TradeWeight Balanced { get; }
    public TradeWeight Growth { get; }
    public CashAccount? CashAccount { get; }

    private readonly HashSet<TradeMopo> _mopos = [];
    public IReadOnlyCollection<TradeMopo> Mopos => _mopos;

    private readonly HashSet<TradeCurrency> _currencies = [];
    public IReadOnlyCollection<TradeCurrency> Currencies => _currencies;

    private Trade(
        Guid id,
        InstrumentId instrumentId,
        TradeWeight reddito,
        TradeWeight income,
        TradeWeight balanced,
        TradeWeight growth,
        CashAccount? cash,
        HashSet<TradeMopo> mopos,
        HashSet<TradeCurrency> currencies)
    {
        Id = id;
        InstrumentId = instrumentId;
        RedditoDifensivo = reddito;
        Income = income;
        Balanced = balanced;
        Growth = growth;
        CashAccount = cash;
        UpdateTradeCurrencies(currencies);
        UpdateTradeMopos(mopos);
    }

    internal static Trade Create(InstrumentId instrumentId,
       TradeWeight reddito,
       TradeWeight income,
       TradeWeight balanced,
       TradeWeight growth,
       CashAccount? cash,
       HashSet<TradeMopo> mopos,
       HashSet<TradeCurrency> currencies)
       => new(Guid.NewGuid(), instrumentId, reddito, income, balanced, growth, cash, mopos, currencies);

    private void UpdateTradeMopos(HashSet<TradeMopo> mopos)
    {
        if (mopos is null)
        {
            throw new InvalidOperationException("It looks like you haven't provided any mopo. Please select at least one mopo to continue.");
        }

        if (mopos.Count < 1)
        {
            throw new InvalidOperationException("You need to include at least one mopo. Please select one or more mopos and try again.");
        }

        _mopos.Clear();
        foreach (var mopo in mopos)
        {
            _mopos.Add(mopo);
        }
    }

    private void UpdateTradeCurrencies(HashSet<TradeCurrency> currencies)
    {
        if (currencies is null)
        {
            throw new InvalidOperationException("It looks like you haven't provided any currency. Please select at least one currency to continue.");
        }

        if (currencies.Count < 1)
        {
            throw new InvalidOperationException("You need to include at least one currency. Please select one or more currencies and try again.");
        }

        _currencies.Clear();
        foreach (var cur in currencies)
        {
            _currencies.Add(cur);
        }
    }
}


